DROP TABLE IF EXISTS `iom_smv_unsend_news`;
CREATE TABLE `iom_camp_unsend_news` (
  `ID` decimal(20,0) NOT NULL COMMENT '主键',
  `NEWS_TIME` datetime DEFAULT NULL COMMENT '消息时间',
  `NEWS_INFO` text NOT NULL COMMENT '信息内容',
  `NEWS_TYPE` int(1) NOT NULL COMMENT '1 日程提醒 2待办(交接信息)3通知',
  `RECEIVER_ID` decimal(20,0) NOT NULL COMMENT '接收人员',
  `CZRY_ID` decimal(20,0) NOT NULL COMMENT '发送人ID',
  `CZRY_DM` varchar(45) DEFAULT NULL COMMENT '人员代码',
  `CZRY_MC` varchar(45) DEFAULT NULL COMMENT '人员名称',
  `CJSJ` datetime NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='未推送消息表';

