DROP TABLE IF EXISTS `iom_camp_custom_menu`;
CREATE TABLE `iom_camp_custom_menu` (
  `ID` decimal(20,0) NOT NULL COMMENT '主键',
  `MENU_ID` decimal(20,0) NOT NULL COMMENT '场景菜单ID，存放系统功能菜单ID或自定义场景ID或外部应用ID',
  `MENU_TYPE` int(11) NOT NULL COMMENT '菜单类型  1 自定义场景页面2外部应用3系统功能菜单',
  `P_MENU_ID` decimal(20,0) DEFAULT NULL COMMENT '上级菜单ID（自定义菜单上下级关系），菜单类型时1,2时此字段有数据',
  `P_FUNC_MENU_ID` decimal(20,0) DEFAULT NULL COMMENT '上级功能菜单ID对应菜单表中的ID，只有菜单类型是3时，此字段有数据',
  `URL` varchar(128) DEFAULT NULL COMMENT 'URL地址',
  `MENU_LEVEL` int(11) DEFAULT NULL COMMENT '菜单级别',
  `SORT` int(11) DEFAULT NULL COMMENT '序号',
  `CJR_ID` decimal(20,0) NOT NULL COMMENT '创建人ID',
  `CJSJ` datetime NOT NULL COMMENT '创建时间',
  `XGR_ID` decimal(20,0) DEFAULT NULL COMMENT '修改人ID',
  `XGSJ` datetime DEFAULT NULL COMMENT '修改时间',
  `YXBZ` int(1) NOT NULL COMMENT '有效标志',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='自定义常用菜单表\n在8004服务中创建\n存储用户自定义的菜单，包括系统已有功能模块、自定义的场景、外部应用';

