DROP TABLE IF EXISTS `iom_camp_interface_market`;
CREATE TABLE `iom_camp_interface_market` (
  `id` decimal(20,0) NOT NULL COMMENT '主键',
  `client_name` varchar(64) DEFAULT NULL COMMENT '厂商名称',
  `client_desc` varchar(64) DEFAULT NULL COMMENT '厂商描述',
  `interface_method` varchar(32) DEFAULT NULL COMMENT '接口方法类型',
  `interface_type` varchar(32) DEFAULT NULL COMMENT '接口类型',
  `interface_uri` varchar(32) DEFAULT NULL COMMENT '接口uri动态部分',
  `username` varchar(64) DEFAULT NULL COMMENT '接口核对名称',
  `password` varchar(64) DEFAULT NULL COMMENT '接口核对密码',
  `interface_param_json` text DEFAULT NULL COMMENT '接口参数json串',
  `yxbz`                 int(1) not null comment '有效标志',
  `cjr_id` decimal(20,0) NOT NULL COMMENT '创建人',
  `cjsj` datetime NOT NULL COMMENT '创建时间',
  `xgr_id` decimal(20,0) DEFAULT NULL COMMENT '修改人',
  `xgsj` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC COMMENT='接口超市';

